sys_ux_data_broker_transform.script:
function transform(input) {
    //replace the following three values with the table names from your instance
    var listTable = "x_1415269_grocery_list";
    var itemTable = "x_1415269_grocery_items";
    var listItemTable = "x_1415269_grocery_list_item";

    var listID = "";
    var au = new global.ArrayUtil();
    var listItemArray = [];
    var groceryItemData = {};

    try {
        //Get the Active list with earliest shopping date
        var list = new GlideRecord(listTable);
        list.addQuery("active", true);
        list.orderBy("shop_date");
        list.query();
        if (list.next()) {
            listID = list.getUniqueValue();

            groceryItemData.listID = listID;
            groceryItemData.items = [];
			gs.info(JSON.stringify(groceryItemData));

            //get the Items that are already on the list.
            var listItem = new GlideRecord(listItemTable);
            listItem.addQuery("list", listID);
            listItem.query();
            while (listItem.next()) {
                listItemArray.push(listItem.item.sys_id + "");
            }

            //query All available items to compare to listItemArray
            var item = new GlideRecord(itemTable);
            item.addQuery("active", true);
            item.orderBy("name");
            item.query();
            while (item.next()) {
                var itemID = item.getUniqueValue();

                //check if Item is already on the list
                var onList = au.contains(listItemArray, itemID);
				var imageURL = item.getValue("image_url");

                var itemObj = {
                    "name": item.getValue("name"),
                    "id": itemID,
                    "added": onList,
					"image": imageURL
                };

                groceryItemData.items.push(itemObj);
            }
            gs.info(JSON.stringify(groceryItemData));

            return groceryItemData;
        }
    } catch (error) {
        return error;
    }
}
